var os = require('os');  
var IPv4,hostName=null;  
hostName=os.hostname();
for(var item1 in os.networkInterfaces()){
  var enInfo = os.networkInterfaces()[item1];
  for(var item2 in enInfo){
    if( enInfo[item2].internal == false && enInfo[item2].family == 'IPv4' ){
      IPv4 = enInfo[item2].address;
      break;
    }
  }
}
module.exports = {
  hostname:hostName,
  ipv4:IPv4
}